package com.arba.sample.rendering;import com.arba.sample.util.AskJdkUtils;import java.util.List;public class ThreadsDrawer extends Thread{    private Integer pid;    public ThreadsDrawer(Integer pid)    {        this.pid = pid;    }    @Override    public void run()    {        parseCmdOutput();    }        private void parseCmdOutput(){        StringBuilder threadSample = new StringBuilder();        boolean isSample = false;        for (String s : AskJdkUtils.getThreadsDumpForProcesses(pid))        {            isSample |= s.startsWith("\"");            if(isSample){                if(s.trim().equals("")){                    System.out.println(threadSample.toString());                    threadSample = new StringBuilder();                    isSample = false;                    continue;                }                threadSample.append(s + "\n");            }        }    }}